#include <iostream>#include <string>#include <windows.h>using namespace std;bool isLuhn(string& NoCard){    int SumDigits = 0;    bool ToDuplicate = false;    for (int i = NoCard.size() - 1; i >= 0; i--)    {        int Digit =  NoCard[i] - '0';        if (ToDuplicate == true) Digit *= 2;        if (Digit > 9) Digit -=9;        SumDigits += Digit;        ToDuplicate = !ToDuplicate;    }    return (SumDigits % 10 == 0);}int main(){    HANDLE console;    console=GetStdHandle(STD_OUTPUT_HANDLE);    SetConsoleTextAttribute(console,11);    char Rep;    string title = "CREDIT CARD VALIDATOR BY DEV0CB";    int width = title.size() + 4;    int height = 3;    for(int i = 0; i < width; i++) cout << "#";    cout << endl;    cout << "# " << title << " #" << endl;    for(int i = 0; i < width; i++) cout << "#";    cout << endl;    do    {        string A;        cout << endl << "Please enter your card number: " << endl;        cin >> A;        if (isLuhn(A)) cout << endl << "This is a valid card" << endl;        else cout << endl << "This is not a valid card" << endl;        cout << endl << "Do you want to try again?" << endl << "[Y/n] :";        cin >> Rep;    }    while (Rep == 'Y');    return 0;}