#include <iostream>#include <string>using namespace std;bool isLuhn(string& NoCard){    int NbDigits = NoCard.length();    int SumDigits = 0;    bool ToDuplicate = false;    for (int i = NbDigits - 1; i >= 0; i--)    {        int Digit =  NoCard[i] - '0';        if (ToDuplicate == true) Digit *= 2;        SumDigits += Digit / 10;        SumDigits += Digit % 10;        ToDuplicate = !ToDuplicate;    }    return (SumDigits % 10 == 0);}int main(){    string A;    cout << "Please enter your card number: " << endl;    cin >> A;    if (isLuhn(A)) cout << "This is a valid card";    else cout << "This is not a valid card";    return 0;}