#include <iostream>#include <string>using namespace std;bool Luhn(string& NumCarte){    int NbreChiffres = NumCarte.length();    int SomChiffres = 0;    bool Doubler = false;    for (int i = NbreChiffres - 1; i >= 0; i--)    {        int ChiffreIndexe =  NumCarte[i] - '0';        if (Doubler == true) ChiffreIndexe *= 2;        SomChiffres += ChiffreIndexe / 10;        SomChiffres += ChiffreIndexe % 10;        Doubler = !Doubler;    }    return (SomChiffres % 10 == 0);}int main(){    string NumCC;    cout << "Veuillez entrer votre numero de carte de credit: " << endl;    cin >> NumCC;    if (Luhn(NumCC)) cout << "C'est une carte valide";    else cout << "Ce n'est pas une carte valide";    return 0;}